CREATE TABLE IF NOT EXISTS `clubs` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `master_id` int(10) unsigned NOT NULL DEFAULT '0',
  `name` varchar(256) NOT NULL,
  `introduction` text NOT NULL,
  `leader` varchar(256) NOT NULL,
  `leader_phone` varchar(256) NOT NULL,
  `leader_email` varchar(256) NOT NULL,
  `leader_msn` varchar(256) NOT NULL,
  `leader_binary` varchar(256) NOT NULL,
  `viceleader` varchar(256) NOT NULL,
  `viceleader_phone` varchar(256) NOT NULL,
  `viceleader_email` varchar(256) NOT NULL,
  `viceleader_msn` varchar(256) NOT NULL,
  `viceleader_binary` varchar(256) NOT NULL,
  `website` text NOT NULL,
  `category` tinyint(1) unsigned NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=109 ;


