INSERT INTO `game_items` (`id`, `category`, `filename`, `name`, `description`, `price`, `level`) VALUES
(1, 1, 'f76789552c45193b2bd7f9aff19468f2', '國王的頭髮', '是很茂密且有光澤的頭髮，可是好像只有非常聰明的人才可以看的到耶', 5000, 1),
(2, 1, '90befea6bf8065e7930fa6e066480962', '夢幻公主頭', '據說是13世紀公主國內實施著言個的髮禁，所有男人嚴格規定都要留這種髮型', 6000, 1),
(3, 1, '51f3deee51225c58e932d9c0f106d826', '墨墨頭', '想法來自一個古老的書法家。一天突發奇想，想用頭髮來寫個曠世鉅作，這種髮型便是他將頭髮沾上墨汁後的髮型', 6000, 1),
(4, 1, '23fe633e22e645a6ed14f35fed5252c0', '綠藻頭', '由綠藻球煉製而成的染料所製散發出綠色光芒的頭髮。戴上他會全身充滿力量，可是副作用好像是會迷路', 10000, 5),
(5, 1, '95728fcabcc6cdeafac6d2bd951804be', '藍‧青鳥', '很久以前聽說這種頭髮是某種稀有的鳥的頭髮，據說可以帶給人們一輩子的幸福', 10000, 5),
(6, 1, 'b9400ddd00b9a675bda2f95e6c2ce1d6', '夢露毛毛頭', '微捲而俐落的粉紅色短髮。據說可以吸引蚊子，一天可以將50隻蚊子捲入那頭髮的夾層中', 10000, 5),
(7, 1, '853012cbd2ab77071f437a417c72e490', '脫韁的野馬尾', '青春的象徵之一，是學生很喜歡的髮型。但聽說經過特殊的咒語加持後，馬尾可以像真的馬的尾巴一樣動起來', 15000, 8),
(8, 1, 'bca6728d41a244c976900c56c237639e', '金光閃閃頭', '充滿金色光芒的頭髮，是由黃金鍊球菌經千年一萬度高溫與高壓殺菌而製成的染料染色的頭髮', 20000, 10),
(9, 1, '4ad176540060184dcc75b3f5e0776d9e', '火炎焱燚‧頭', '傳說中火神所遺留下來的假髮，戴上他會感覺到彷彿火神加持，全身充滿熱血而不斷流汗，至少冷氣要開到-10度才會感覺涼', 30000, 12),
(10, 1, '01d2437bc8873f73a5c205356be22901', '矛射‧盾開', '相傳是獵人部族內避險的聖物。被熊追殺時，戴上他並瞬間倒下裝死，保證熊看到一定會給你一巴掌並說：「你給我裝死啊！」', 30000, 12),
(11, 1, 'a6eaf46ed6ab7144c428137ab0d6e175', '針鋒相對‧死對頭', '傳說中邪惡的髮型，在向別人敬禮時，趁機以頭上的針刺給敵人效果超群的命中要害。可能是三國時代極為重要的暗殺工具', 25000, 12),
(12, 1, '8f8a84c4223c13a8dfa275f6d7d9676b', '偷吃人家豆腐', '古代最強吃豆腐戰士留下來的最帥氣髮型，傳說只要配上這種髮型全世界的異性都將給虧，進而吃遍天下所有豆腐', 30000, 15),
(13, 1, '17dae054ed181ac05ce7983714d0031f', '真直的頭髮', '傳聞中只要擁有這一個髮型就能獲得可以從任何一台電視中爬出來的能力', 40000, 15),
(14, 1, 'a14de0a821c26580a29e90711753b4f3', '千年兔子精', '據說是某部落傳說中的獵人，獵殺兔子族的千年老妖精後所製作出來具有千年力量的頭飾', 60000, 17),
(15, 1, '7460295c7cc2e01624a84975d11b3bd3', '小叮‧當你升天', '當紅著名電視男主角之一，深受大人小孩的喜愛。這是他最常戴的帽子，據說，他擁有很多頂，不見了還可以繼續拿到一模一樣的', 40000, 17),
(16, 1, '7143204ebc85bcc1124a6117f41ae596', '神‧七彩霓虹頭', '狂野又流行的髮型，是從open將的彩虹頭飾所引發的奇特想法，說不是會是神界未來流行的染髮方式', 150000, 18),
(17, 1, '67843cf27edaf058dc5120bbf7cc8833', '荒天攝雲‧頭', '神界中流傳的最終兵器的一部分。散發著宇宙尊者的光輝，只有被選重的人才得以獲得這帝王之聖者的尊榮', 200000, 20),
(18, 1, '3ab1e1f4e24ed8f4a58ffa308cb03cd5', '流霜浣泉‧頭', '神界中流傳的最終兵器的一部分。散發著宇宙尊者的光輝，只有被選重的人才得以獲得這帝王之聖者的尊榮', 200000, 20),
(19, 2, 'ca07e580d61c17951b2252701798969c', '天真陽光丁丁臉', '可以帶給人們溫暖快樂的表情，如天使般純真的微笑，如丁丁是個人才一般的傻笑。', 6000, 1),
(20, 2, '7d51284d2cdad516bc348104a1d1805e', '真‧三國無名臉', '傳說是三國時期最受歡迎的妝容之一。有每當塗上它，連自己的爸媽都會誤認為是個無名氏的神奇力量', 10000, 1),
(21, 2, 'f7a7fa536448b799c686a14e92cf77ee', '空洞‧腦洞', '像是被催狂魔之吻吸走智商一樣，所以臉上除了呆滯剩下的只有蠢', 10000, 1),
(22, 2, '41bd1cb0f5e7fc3004c119412a399887', '快樂似神清氣爽', '人說快樂似神仙，而這就是模仿出仙風道骨般的表情。有人說，可以任人得到一眨眼就可以引發涼爽的颶風的神力', 10000, 1),
(23, 2, '88e15677b26857e13ab1bd4ea99f2fb6', '咦咦咦‧滿腹狐疑', '我在說什麼?我在做什麼?我怎麼聽不懂?你問我在說什麼?我怎麼不知道你在問我什麼？', 18000, 5),
(24, 2, 'ab8f56301214e55e906e68da171391f6', '請問哪裡有廁所', '快要尿出來的時候大部分人所顯露的表情。但據說這是某部落跟別人說Sorry時硬擺出的表情，否則會被認為是不禮貌的', 18000, 5),
(25, 2, 'e6919ff7c6950dd371284c84ca376a54', '不停揉眼睛', '媽媽說：「別再揉眼睛了，再揉就要變成兔子的眼睛了！」。原來揉眼睛就會變成這樣子啊！', 24000, 9),
(26, 2, '92d19cd2321e6f326e1153f846e15c4e', '藍眼‧難掩的憂傷', '散發出神秘藍色光芒的眼球，讓所有與這雙眼睛相視的人瞬間感到滿腹難掩的憂傷，想要馬上上廁所', 24000, 9),
(27, 2, '70153c8eb9526cb1e12d58886295e95d', '去味清香綠眼睛', '綠眼睛，綠眼睛，大家都愛綠眼睛。哥哥姐姐妹妹都愛綠眼睛，去味清香綠眼睛。人見人愛的綠眼睛喔！', 24000, 9),
(28, 2, '85267dc3d9f3a21e1a43a8e5c6cecf19', '冰山冷冽魅力電眼', '冰山美女的電眼，效果超群。可以放出十萬伏特電倒所有與自己對到眼的北極熊、企鵝或是海豹', 28000, 12),
(29, 2, '41668da2729dd1bf56a08744089521cb', '大冒險的後果', '可惡，輸了，又要大冒險被人家用奇異筆畫臉，每次要洗都很難洗掉，有夠丟臉的啦！', 30000, 12),
(30, 2, '24b6ae55f40bdbbb4fdb32a841270b3f', '小丑‧小醜', '被蝙蝠俠打敗後，小丑實在難過得無法自拔，無以復加。最後劃出最小丑的小醜眼線', 30000, 12),
(31, 2, '246455e1c1302bafa4d8923efecb5900', '水庫潰堤‧汪汪眼', '水汪汪的眼睛如同快要潰堤的水庫，而這就是剛被爸媽處罰快要哭出來之前的徵兆', 40000, 15),
(32, 2, '4aec95444246891e1a494fa93866c4dc', '朦朦朧朧迷幻眼', '俗話說，朦朧就是美。剛睡醒的時候朦朧的眼睛帶點迷幻，可以讓你心儀的對象目眩神迷', 40000, 15),
(33, 2, '8a1996ebcbe4033af8bf6df2f1bb9189', '你可以再靠近一眼', '你在看我嗎?我知道你在看我。你可以再靠近一點，再多看我一眼，你可以再靠近一眼', 40000, 15),
(34, 2, '8ebf855a983099ad2269cf07d80684e9', '獨眼‧屎派螺', '傳說中的海盜，屎派螺船長。海盜們為了能夠再次崇仰他的容貌，所以複製了他的面容。如今這在海盜界流傳甚廣', 40000, 15),
(35, 2, '7ea78d9b71efaf55234c10429c7cbc63', '睡醒看不清', '剛睡醒嗎？怎麼看不清楚我長什麼樣子？去洗洗臉吧，說不定就可以看清楚我的容貌喔！', 54000, 17),
(36, 2, '172b53df692395d8819179fbaa77bf26', '荒天攝雲‧眼', '神界中流傳的最終兵器的一部分。散發著宇宙尊者的光輝，只有被選重的人才得以獲得這帝王之聖者的尊榮', 200000, 20),
(37, 2, '05489ba5d9c52bac75c850eaacbe7f58', '流霜浣泉‧眼', '神界中流傳的最終兵器的一部分。散發著宇宙尊者的光輝，只有被選重的人才得以獲得這帝王之聖者的尊榮', 200000, 20),
(38, 3, '4bc602baca4309401917dc89c040c95b', '碧風‧揮揮衣袖', '揮一揮衣袖，帶走一片雲彩，引來一堆無賴，是個招來爛桃花運的最佳利器', 8000, 1),
(39, 3, '0fed7f93d5460bdbb2014393bd865d28', '抗LV防曬襯衫', '未來的衣服，經由時空的裂縫遺留到現代。聽說可以抵抗未來發現的另一種有害物質，LV，的超有效襯衫。價值不斐啊', 15000, 1),
(40, 3, '08d71c9c04127ba1b1e35e3eca54f7fe', '人蔘伸深深 V', '謠傳人蔘原為一美麗女子，其深V領的裝扮，延長不少男性壽命。為表揚此舉，之後世人只要穿上此衣變象徵擁有傷風敗俗的高尚情操', 25000, 5),
(41, 3, 'e7ee31ce00e350e912a18338d5ed1009', '揚帆‧鐵打泥', '如夢如幻的神之方舟，鐵打泥，據說只會在人們的夢中出現。一旦在夢中看他揚起它的帆，就可以實現你三個夢想喔 ！', 27000, 5),
(42, 3, 'ae0d90ae9dd3c6bb3d251fb653367a52', '被弄髒的黑衣服', '為少林盟主乞丐幫幫主所特有的金縷破衣，穿上它會讓妳全身散發出一種嘿嘿嘿的鬼魅氣息。到時擂倒長輩，迷倒晚輩，天下都很背', 27000, 5),
(43, 3, '792824de9971f43f984a950b734de286', '聖‧白衣天使', '由南丁格爾家世代流傳傳家寶，平常功用是防蚊子叮，絕招─立刻讓你成為眾人的眼中釘', 30000, 10),
(44, 3, '6c95a392dbd908f05b1401e857cd343b', '有驚無險驚喜衫', '一個富有奇怪能力的衣服。讓你隨時走路都會讓你跌倒，卻又保護你不會受傷。最厲害的是，每次跌倒都能獲得不一樣的東西，讓生活充滿驚喜', 36000, 10),
(45, 3, '9c338caaf12209c4a3e872eaeeb91409', '古道‧西風‧瘦馬', '著名服飾設計家在一天看見小橋流水，秋風拂面，絕頂樓閣的絕美景色後靈光一動後設計出的轟動之作', 44000, 13),
(46, 3, '6666eece26236a5d84e22f26356a2247', '蒙面‧低‧滷蛋', '如神話般的拳擊手，永遠蒙著面並低著頭，最喜歡的食物就是魯蛋。他一心只求有人能打敗他，就要把他身上的富含維他命Ｃ的衣服送給他', 44000, 13),
(47, 3, '1c8870079907926af246e0d2852bd98a', '火炎焱燚‧衣', '傳說中火神所遺留下來的神奇鎧甲，穿上他會感覺到彷彿火神加持，全身不斷發熱。可能就是最近很紅的「發熱衣」的構思來源', 50000, 15),
(48, 3, '2e1057865251e4e32a925cacb8275454', '雉聖‧仙絲', '以神話中古代的鳥類「雉聖」的羽毛所做成的仙絲所織成的仙衣，可以減少重量，讓人活動起來更無負擔。最受歡迎的使用方法是穿上它，並站上體重計', 55000, 15),
(49, 3, '8d2080d0d821a89d7f3b02322db50b7b', '養樂多啦一場夢', '當紅著名電視男主角之一，深受大人小孩的喜愛。這是他最常穿的衣服，據說，他家的衣櫃內總共只有七件衣服，七件一模一樣的衣服', 60000, 17),
(50, 3, '6fbd9bd52919da38cdefe6afd860d0dd', '鮮度銳拉‧保鮮膜', '美味的餐點最需要的就是新鮮的食材，新鮮的食材最需要的就是保鮮膜。這款保鮮膜可以讓您的食材的鮮度以銳利曲線的拉升', 120000, 18),
(51, 3, '746f440eab94b39fab55b54d1a05eea5', '荒天攝雲‧衣', '神界中流傳的最終兵器的一部分。散發著宇宙尊者的光輝，只有被選重的人才得以獲得這帝王之聖者的尊榮', 200000, 20),
(52, 3, 'd2c0d865980167e58624b64fa3126727', '流霜浣泉‧衣', '神界中流傳的最終兵器的一部分。散發著宇宙尊者的光輝，只有被選重的人才得以獲得這帝王之聖者的尊榮', 200000, 20),
(53, 4, 'a4490ab53f43db4975d4e6d2c77cc700', '天然晶礦‧泉擊褲', '這件褲子是用吸收了天地精華的天然晶礦所製成，穿上它能用天然礦泉水時時刻刻補充流失掉的水分。', 10000, 1),
(54, 4, 'dd3b112704c93ab4efb956de1ef2bc23', '麻煩請你往下看', '看哪裡啊，往下面看啦！我是說我的鞋子很好看啦，你在想什麼？', 12000, 1),
(55, 4, '752ca5b8c216b2eec9ea8b3817ac94b7', '米缸沒有米‧彩褲', '古代神農氏的聖物。傳說祂擔心人們會受到饑荒所苦，在人間留下這件聖物。只要穿上它，就可以源源不絕的從嘴巴吐出白米', 19000, 3),
(56, 4, '4c2418024b1e2587bfd59807e64ba4e1', '補路技師‧褲甲', '似乎可以讓工作能力瞬間提升至極限的褲子。而現在的牛仔褲似乎就是以這件褲子的名稱命名的', 19000, 3),
(57, 4, '61aa273104ed6c193a9b46ab49c0bae3', '曝光up up短裙', '以傳說中的絲綢製作，非常的輕盈，所以只要輕輕的吹氣就可以飄揚空中。想要讓曝光度破錶嗎？試試這款短裙吧！', 60000, 7),
(58, 4, '5ec060372eb6d681dbc58e993a2a472d', '國父遺留的聖物‧左', '古代民間流傳，國父革命時隨時守護的兩大護衛之一的左使者所穿的褲子，據說能夠得到無堅不摧的巨大力量', 24040, 8),
(59, 4, '80cb6bbf0c8c7020c556d0249e10e9b7', '國父遺留的聖物‧右', '古代民間流傳，國父革命時隨時守護的兩大護衛之一的右使者所穿的褲子，據說能夠得到神靈護體的巨大力量', 24242, 8),
(60, 4, 'e13c884222e08a9b2fb6b1bdb48fe53f', '嘻嘻哈哈‧嘻哈褲', '只要穿上這件褲子，可以感覺到有很多手不斷在褲子裡搔癢，讓你整天嘻嘻哈哈，笑容與你常在！', 25000, 8),
(61, 4, 'd16944e6566732e353cf1617187e1efc', '中秋節雙筒燈籠', '有了它，中秋節就不需要用手提用燈籠了！有了它，天天都是中秋節，不用等到中秋節才能閃亮亮喔！', 28000, 11),
(62, 4, '414dcd369e5d60aa94cf80d0f0c49792', '火炎焱燚‧褲', '傳說中火神所遺留下來的千年褲子，穿上他會感覺到彷彿火神加持。有如穿著一團火球一般，讓雙腳不斷揮舞，像熱鍋上的螞蟻', 36000, 13),
(63, 4, 'f14454c9ffdf96cdb87184aea2801ab7', '虛與‧夏‧委蛇草褲', '幾億年前的古代民族，虛與族，的傳統服飾。利用「委蛇」的蛇皮所製成的草褲，是夏天的常見的服飾', 40000, 15),
(64, 4, '83d966b27da098dc0b19671943a9902d', '奇多的蠟筆夢', '當紅著名電視男主角之一，深受大人小孩的喜愛。這是他最常穿的褲子，據說，他家的衣櫃內總共只有七件褲子，七件一模一樣的褲子。', 50000, 17),
(65, 4, '64a9c4b82d554df4d60b558bb689d902', '限制級戰緊身褲甲', '限制１８歲以下才可以穿著。人說物極必反，所以傳說中緊身的戰甲，一定是回歸最原始的毫無拘束。', 120000, 18),
(66, 4, '633120b29ac2be481025c195cf1ae839', '荒天攝雲‧褲', '神界中流傳的最終兵器的一部分。散發著宇宙尊者的光輝，只有被選重的人才得以獲得這帝王之聖者的尊榮', 200000, 20),
(67, 4, '251cd1ba4583a1cebae41ef639ea0aff', '流霜浣泉‧裙', '神界中流傳的最終兵器的一部分。散發著宇宙尊者的光輝，只有被選重的人才得以獲得這帝王之聖者的尊榮', 200000, 20),
(68, 5, '1aa6a3a70423f690fecc21f7e241db04', '藍白‧煞氣武靴', '據說早期軍官出兵都穿藍白拖。一次滑倒煞到氣，決定改良為武靴。從此經過之處，其走路翩翩起舞無不煞到周圍的人，因此得來煞氣武靴的封號', 10000, 1),
(69, 5, '90f4dfcd8cc45edad70c06997973a4b0', '小紅鞋‧紅帽', '聽說小紅帽童話故事的發想來源就是來自一位穿著這雙被詛咒的紅鞋的女孩。一但穿上他，右腳就會不由自主的開始踩自己的左腳', 16000, 1),
(70, 5, '8f53ee3296a55b4b0aa34740afe5d569', '酷激天天藍', '如蔚藍海域般充滿又酷又刺激的生活，想要成為海賊王，穿上它保證讓你天天都躝婪懶', 22000, 6),
(71, 5, '8e66e847e09fde0e6d5c0669fbc31746', '綠西瓜‧綠蠵龜', '烏龜行動遲緩，這雙鞋讓你步調放慢，優游自在，體驗到品嘗西瓜漫步天涯的休閒生活', 24000, 6),
(72, 5, 'aa4b4fef75f6b40f5a84923c1e7d1835', '耐肌氣墊鞋', '打敗NIKE氣墊，不管肌耐力、機耐力還是雞耐力皆大幅提升，蛻化為一個畸形的完全變態超人。舌頭舔到脖子，屁股碰到耳根子，都不再是問題', 26000, 6),
(73, 5, '05bcddf36e5ff3a4a9f6d9169e53331c', '三吋‧金線蓮', '偉大的傳統中國文化中，三寸金蓮的美麗金線鞋，婀娜的曼妙步伐彷彿踏著蓮花布，迷倒眾生絕不能少了它', 26000, 6),
(74, 5, '8f2b31eb29529e6194ab4b4248e4776f', '削你耳‧木蘭靴', '口耳相傳的製靴好口碑，削你耳，聽說是魏晉南北朝最有名的製鞋專家。有人說，穿上他們家的鞋子，木蘭也可以跑的像馬一樣快', 28000, 6),
(75, 5, '889024680b66661d19b38625726bf3e2', '尖尖‧彎彎', '傳說中，月娘的聖靴。可以增加跑步速度，是跑步選手夢寐以求的聖物。無聊的時候也可以拿起來賞月，也別有一番風味。', 34000, 15),
(76, 5, '453b58e572b9a81edcb26ba539c75a7d', '滑呀‧滑溜滑溜冰鞋', '滑到北極南極，上通天地，下達地獄，歷經七七四十九天，終於練成了滑稽冰棒鞋。無論搞笑或食用都是相當不錯的考量，一舉兩得喲!', 70000, 15),
(77, 5, '8efe2f7ff42b67f38a1bb347d541da27', '綁定‧纏腳帶', '結合阿嬤的裹腳布、達悟族的丁字褲，又臭又長又牢固，包準嚇的敵人各個變白目', 100000, 17),
(78, 5, '1557dbd15c83e88ba07a5bb64ce49e29', '哆 La Si Do A 夢', '當紅著名電視男主角之一，深受大人小孩的喜愛。這是他最常穿的鞋子，據說，他家的衣櫃內總共只有兩雙，一雙是拖鞋，一雙是布鞋。', 30000, 18),
(79, 5, 'f63b45a3e574ce0d9eb9497b4108e75c', '荒天攝雲‧鞋', '神界中流傳的最終兵器的一部分。散發著宇宙尊者的光輝，只有被選重的人才得以獲得這帝王之聖者的尊榮', 200000, 20),
